mapper.insert:
/* 회원가입 */
INSERT INTO USERS(user_no, user_name, user_id, user_pw, user_email, user_phone, user_birth, user_type, user_date)
VALUES (users_seq.nextval, #{userName}, #{userId}, #{userPw}, #{userEmail}, #{userPhone}, #{userBirth}, 'normal',sysdate)

mapper.select:
/* 아이디중복확인 */
SELECT count(*)
FROM USERS
WHERE user_id=#{userId}

mapper.update:
/*정보 수정 */
UPDATE USERS  
SET	user_pw=#{userPw}, user_phone=#{userPhone}
WHERE user_pw=#{userPw}
